var jqCommon={placeholderEmpty:"jqCommEmpty",stopBubble:function(e){var e=e?e:window.event;if(window.event){e.cancelBubble=true;}else{e.stopPropagation();}},checkPlaceholder:function(input,text){if(input.val()==""){input.addClass(jqCommon.placeholderEmpty);input.val(text);}},cleanPlaceholder:function(input){if(input.hasClass(jqCommon.placeholderEmpty)){input.removeClass(jqCommon.placeholderEmpty);input.val("");}},setPlaceholder:function(formSelector,inputSelector,text){var form=$("form"+formSelector);var input=$("input"+inputSelector);if(input.val()==""||input.val()==text){input.addClass(jqCommon.placeholderEmpty);input.val(text);}
input.focus(function(){jqCommon.cleanPlaceholder($(this));});input.click(function(){jqCommon.cleanPlaceholder($(this));});input.blur(function(){jqCommon.checkPlaceholder($(this),text);});form.submit(function(){if(input.hasClass(jqCommon.placeholderEmpty)){input.val("");}});}}